import "./App.scss"
import { ReactComponent as Add } from "./assets/icons/add.svg"
import AddEditTaskForm from "./components/AddEditTaskForm"
import Button from "./components/Button"
import DeleteModal from "./components/DeleteModal"
import TaskCard from "./components/TaskCard"
import { taskList } from "./siteData/taskList"
import Modal from "./components/Modal"
import { useEffect, useState } from "react"

const App = () => {
  const [selectPriority, setSelectPriority] = useState("")
  // const showAddEditModal = false
  // const showDeleteModal = false
  const [showAddEditModal, setShowAddEditModal] = useState(false)
  // const actionSetShowAddEditModal = setShowAddEditModal

  const [task, setTask] = useState("")
  const [taskKey, setTaskKey] = useState("")

  const setHandleTask = (e?: any) => {
    console.log(e)
    setTask(e)
  }

  const trocaExibicaoModal = () => {
    setShowAddEditModal(!showAddEditModal)
  }
  const alternarPrioridades = (priority?: any) => {
    setSelectPriority(priority)

    setTaskKey(priority)
  }

  useEffect(() => {
    setHandleTask()
  }, [task])


  return (
    <div className="container">
      <div className="page-wrapper">
        <div className="top-title">
          <h2>Lista de Tarefas</h2>
          <Button title="Add Task" icon={<Add />} onClick={() => { trocaExibicaoModal() }} />
        </div>

        {showAddEditModal ? (
          <Modal children={ AddEditTaskForm(trocaExibicaoModal, selectPriority, alternarPrioridades, task, setHandleTask, taskKey) } />
        ) : null }

        <div className="task-container">
          {taskList.map((task) => (
            <TaskCard task={task} />
          ))}
        </div>
      </div>
      {/* {showAddEditModal && <AddEditTaskForm />} */}
      {/* {showDeleteModal && <DeleteModal />} */}
    </div>
  )
}

export default App
