import classNames from "classnames"
import { ReactComponent as Close } from "../../assets/icons/close.svg"
import Button from "../Button"
import Input from "../Input"
import Modal from "../Modal"
import "./style.scss"
import axios from "axios"
// import { useState } from "react"

const AddEditTaskForm = (trocaExibicaoModal:()=> void, selectPriority: string, alternarPrioridades:(priority?: any) => void, task: any, setHandleTask:(e?: any) => void, taskKey: any, selectTasks:()=> any) => {


  const addTasks = () => {
    axios.post("http://localhost:59000/create", {"task": task, "priority": taskKey}).then( (res) => {
      trocaExibicaoModal()

      selectTasks()
    })
  }

  return (
    <Modal>
      <form>
        <div className="add-edit-modal">
          <div className="flx-between">
            <span className="modal-title">Adicionar Tarefa</span>
            <Close className="cp" onClick={() => { trocaExibicaoModal()}}/>
          </div>
          <Input label="Tarefa" placeholder="Digite sua tarefa aqui..." onChange={(e) => {setHandleTask(e.target.value)}} name="title" defaultValue="" />
          <div className="modal-priority">
            <span>Priority</span>
            <ul className="priority-buttons">
              {["high", "medium", "low"].map((priority) => {

                if (selectPriority === priority) {
                  return (
                  <li key={priority} className={classNames(`${selectPriority}-selected`, priority)} onClick={ () => alternarPrioridades(priority)}>
                    {priority}
                  </li>
                  )
                }else {
                  return (
                  <li key={priority} className={classNames(``, priority)} onClick={ () => alternarPrioridades(priority)}>
                    {priority}
                  </li>
                  )
                }

              })}
            </ul>
          </div>
          <div className="flx-right mt-50">
            <Button title="Add" onClick={() => {addTasks()}} />
          </div>
        </div>
      </form>
    </Modal>
  )
}

export default AddEditTaskForm
