import "./App.scss"
import { ReactComponent as Add } from "./assets/icons/add.svg"
import AddEditTaskForm from "./components/AddEditTaskForm"
import Button from "./components/Button"
import DeleteModal from "./components/DeleteModal"
import TaskCard from "./components/TaskCard"
// import { taskList } from "./siteData/taskList"
import Modal from "./components/Modal"
import { useEffect, useState } from "react"
import axios from "axios"

const App = () => {
  // const [taskId, setTaskId] = useState("")
  const [taskList, setTaskList] = useState([])

  const deleteTask = async (_id?: any) => {
    if (_id && _id != undefined) {
      // setTaskId(_id)

      console.log(_id)

      // await axios.delete(`http://localhost:59000/delete/${_id}`).then( (res) => {
      //   // setTaskList(res.data)
      //   console.log("deletandooooooo")
      // })
    }
  }

  const selectTasks = async () => {
    await axios.post("http://localhost:59000/read").then( (res) => {
      setTaskList(res.data)
    })

    return false
  }

  const [selectPriority, setSelectPriority] = useState("")
  // const showAddEditModal = false
  // const showDeleteModal = false
  const [showAddEditModal, setShowAddEditModal] = useState(false)
  // const actionSetShowAddEditModal = setShowAddEditModal

  const [task, setTask] = useState("")
  const [taskKey, setTaskKey] = useState("")

  const setHandleTask = (e?: any) => {
    if (e != undefined) {
      setTask(e)
    }
  }

  const trocaExibicaoModal = () => {
    setShowAddEditModal(!showAddEditModal)
  }
  const alternarPrioridades = (priority?: any) => {
    setSelectPriority(priority)

    setTaskKey(priority)
  }

  useEffect(() => {
    selectTasks()
  }, [])

  // useEffect(() => {
  //   deleteTask()
  // }, [])

  useEffect(() => {
    setHandleTask()
  }, [task])


  return (
    <div className="container">
      <div className="page-wrapper">
        <div className="top-title">
          <h2>Lista de Tarefas</h2>
          <Button title="Add Task" icon={<Add />} onClick={() => { trocaExibicaoModal() }} />
        </div>

        {showAddEditModal ? (
          <Modal children={ AddEditTaskForm(trocaExibicaoModal, selectPriority, alternarPrioridades, task, setHandleTask, taskKey, selectTasks) } />
        ) : null }

        <div className="task-container">
          {taskList.map((task) => {
            return (
              <TaskCard task={task} deleteTask={deleteTask}/>
            )})
          }
        </div>
      </div>
      {/* {showAddEditModal && <AddEditTaskForm />} */}
      {/* {showDeleteModal && <DeleteModal />} */}
    </div>
  )
}

export default App
